import React, { useState } from 'react';
import axios from 'axios';
import './Register.css';

const Register = ({ onBack, onRegisterSuccess }) => {
  const [registerMethod, setRegisterMethod] = useState('username'); // 'username' or 'phone'
  const [formData, setFormData] = useState({
    fullName: '',
    username: '',
    phone: '',
    password: '',
    confirmPassword: ''
  });
  const [otp, setOtp] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loading, setLoading] = useState(false);
  const [otpSent, setOtpSent] = useState(false);
  const [phoneVerified, setPhoneVerified] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSendOTP = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    // Validate phone number
    if (formData.phone.length !== 10 || !/^[0-9]+$/.test(formData.phone)) {
      setError('Please enter a valid 10-digit phone number');
      return;
    }

    setLoading(true);

    try {
      const response = await axios.post('http://localhost:5000/api/send-otp', {
        phone: formData.phone
      });

      if (response.data.success) {
        setOtpSent(true);
        setSuccess('OTP sent to your phone! Please verify.');
      }
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to send OTP. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleVerifyOTP = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setLoading(true);

    try {
      const response = await axios.post('http://localhost:5000/api/verify-otp', {
        phone: formData.phone,
        otp
      });

      if (response.data.success) {
        setPhoneVerified(true);
        setSuccess('Phone verified successfully!');
        setOtpSent(false);
      }
    } catch (err) {
      setError(err.response?.data?.error || 'Invalid OTP. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleRegisterUsername = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    // Validation for username/password registration
    if (formData.password !== formData.confirmPassword) {
      setError('Passwords do not match');
      return;
    }

    if (formData.password.length < 6) {
      setError('Password must be at least 6 characters long');
      return;
    }

    setLoading(true);

    try {
      const response = await axios.post('http://localhost:5000/api/register', {
        fullName: formData.fullName,
        username: formData.username,
        phone: null, // No phone for username registration
        password: formData.password
      });

      if (response.data.success) {
        setSuccess('Registration successful! Redirecting to login...');
        setTimeout(() => {
          onRegisterSuccess();
        }, 2000);
      }
    } catch (err) {
      setError(err.response?.data?.error || 'Registration failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleRegisterPhone = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    // Validation for phone registration
    if (!phoneVerified) {
      setError('Please verify your phone number first');
      return;
    }

    if (formData.password !== formData.confirmPassword) {
      setError('Passwords do not match');
      return;
    }

    if (formData.password.length < 6) {
      setError('Password must be at least 6 characters long');
      return;
    }

    setLoading(true);

    try {
      const response = await axios.post('http://localhost:5000/api/register', {
        fullName: formData.fullName,
        username: formData.username,
        phone: formData.phone,
        password: formData.password
      });

      if (response.data.success) {
        setSuccess('Registration successful! Redirecting to login...');
        setTimeout(() => {
          onRegisterSuccess();
        }, 2000);
      }
    } catch (err) {
      setError(err.response?.data?.error || 'Registration failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-container">
      <div className="login-card register-card">
        <div className="login-header">
          <h1>QAdam</h1>
          <p>Create Your Account</p>
        </div>

        <div className="login-method-toggle">
          <button
            type="button"
            className={`toggle-btn ${registerMethod === 'username' ? 'active' : ''}`}
            onClick={() => {
              setRegisterMethod('username');
              setOtpSent(false);
              setPhoneVerified(false);
              setError('');
              setSuccess('');
            }}
          >
            Username/Password
          </button>
          <button
            type="button"
            className={`toggle-btn ${registerMethod === 'phone' ? 'active' : ''}`}
            onClick={() => {
              setRegisterMethod('phone');
              setError('');
              setSuccess('');
            }}
          >
            Phone OTP
          </button>
        </div>

        <form onSubmit={
          registerMethod === 'username' ? handleRegisterUsername : 
          (phoneVerified ? handleRegisterPhone : (otpSent ? handleVerifyOTP : handleSendOTP))
        } className="login-form">
          <div className="form-group">
            <label htmlFor="fullName">Full Name</label>
            <input
              type="text"
              id="fullName"
              name="fullName"
              value={formData.fullName}
              onChange={handleChange}
              placeholder="Enter your full name"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="username">Username</label>
            <input
              type="text"
              id="username"
              name="username"
              value={formData.username}
              onChange={handleChange}
              placeholder="Choose a username"
              required
            />
          </div>

          {registerMethod === 'phone' && (
            <>
              <div className="form-group">
                <label htmlFor="phone">
                  Phone Number
                  {phoneVerified && <span className="verified-badge">âœ“ Verified</span>}
                </label>
                <input
                  type="tel"
                  id="phone"
                  name="phone"
                  value={formData.phone}
                  onChange={handleChange}
                  placeholder="Enter 10-digit phone number"
                  pattern="[0-9]{10}"
                  maxLength="10"
                  required
                  disabled={otpSent || phoneVerified}
                />
              </div>

              {otpSent && !phoneVerified && (
                <div className="form-group">
                  <label htmlFor="otp">Enter OTP</label>
                  <input
                    type="text"
                    id="otp"
                    value={otp}
                    onChange={(e) => setOtp(e.target.value)}
                    placeholder="Enter 6-digit OTP"
                    pattern="[0-9]{6}"
                    maxLength="6"
                    required
                  />
                  <p className="otp-info">OTP sent to your phone. Check console if not received via SMS.</p>
                </div>
              )}
            </>
          )}

          {(registerMethod === 'username' || phoneVerified) && (
            <>
              <div className="form-group">
                <label htmlFor="password">Password</label>
                <input
                  type="password"
                  id="password"
                  name="password"
                  value={formData.password}
                  onChange={handleChange}
                  placeholder="Create a password (min 6 characters)"
                  minLength="6"
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="confirmPassword">Confirm Password</label>
                <input
                  type="password"
                  id="confirmPassword"
                  name="confirmPassword"
                  value={formData.confirmPassword}
                  onChange={handleChange}
                  placeholder="Re-enter your password"
                  minLength="6"
                  required
                />
              </div>
            </>
          )}

          {error && <div className="error-message">{error}</div>}
          {success && <div className="success-message">{success}</div>}

          <button type="submit" className="login-button" disabled={loading}>
            {loading ? 'Processing...' : (
              phoneVerified ? 'Register' : (otpSent ? 'Verify OTP' : 'Send OTP')
            )}
          </button>

          {otpSent && !phoneVerified && (
            <button
              type="button"
              className="resend-otp-btn"
              onClick={() => {
                setOtpSent(false);
                setOtp('');
                setSuccess('');
              }}
            >
              Resend OTP
            </button>
          )}
        </form>

        <div className="register-link">
          <p>
            Already have an account?{' '}
            <button
              type="button"
              className="register-btn"
              onClick={onBack}
            >
              Login
            </button>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Register;
