import React, { useState } from 'react';
import './SingleQuestionUpload.css';

const SingleQuestionUpload = ({ onClose, onQuestionParsed }) => {
  const [inputMethod, setInputMethod] = useState('text'); // 'text', 'paste'
  const [questionText, setQuestionText] = useState('');
  const [pastedImage, setPastedImage] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [parsedQuestion, setParsedQuestion] = useState(null);

  const handlePaste = async (e) => {
    const items = e.clipboardData?.items;
    if (!items) return;

    for (let i = 0; i < items.length; i++) {
      const item = items[i];

      if (item.type.indexOf('image') !== -1) {
        const blob = item.getAsFile();
        if (blob) {
          setPastedImage(blob);
          setError('');

          // Create preview URL
          const reader = new FileReader();
          reader.onload = (event) => {
            // Store the data URL for preview
            setPastedImage({
              blob: blob,
              preview: event.target.result,
              name: `pasted-image-${Date.now()}.png`,
              size: blob.size
            });
          };
          reader.readAsDataURL(blob);

          console.log('Image pasted from clipboard');
          break;
        }
      }
    }
  };

  const handleClearPastedImage = () => {
    setPastedImage(null);
  };

  const handleSubmit = async () => {
    if (inputMethod === 'text' && !questionText.trim()) {
      setError('Please enter question text');
      return;
    }

    if (inputMethod === 'paste' && !pastedImage) {
      setError('Please paste an image (Ctrl+V or Cmd+V)');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const formData = new FormData();

      if (inputMethod === 'text') {
        formData.append('question_text', questionText);
        formData.append('input_type', 'text');
      } else if (inputMethod === 'paste') {
        formData.append('file', pastedImage.blob, pastedImage.name);
        formData.append('input_type', 'file');
        formData.append('file_type', 'png');
      }

      const response = await fetch('http://localhost:5000/api/parse-single-question', {
        method: 'POST',
        body: formData
      });

      const data = await response.json();

      if (data.success) {
        setParsedQuestion(data.question);
        if (onQuestionParsed) {
          onQuestionParsed(data.question);
        }
      } else {
        setError(data.error || 'Failed to parse question');
      }
    } catch (err) {
      setError('Error: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleReset = () => {
    setQuestionText('');
    setPastedImage(null);
    setParsedQuestion(null);
    setError('');
  };

  return (
    <div className="single-question-modal">
      <div className="single-question-modal-content">
        <div className="single-question-header">
          <h2>📝 Upload Single Question</h2>
          <button className="close-btn" onClick={onClose}>✕</button>
        </div>

        {!parsedQuestion ? (
          <>
            <div className="input-method-selector">
              <button
                className={`method-btn ${inputMethod === 'text' ? 'active' : ''}`}
                onClick={() => setInputMethod('text')}
              >
                ✍️ Text Input
              </button>
              <button
                className={`method-btn ${inputMethod === 'paste' ? 'active' : ''}`}
                onClick={() => setInputMethod('paste')}
              >
                📋 Paste Image
              </button>
            </div>

            {inputMethod === 'text' ? (
              <div className="text-input-section">
                <label>Enter Question Text:</label>
                <textarea
                  value={questionText}
                  onChange={(e) => setQuestionText(e.target.value)}
                  placeholder="Type or paste your question here...&#10;&#10;Example:&#10;1. What is the capital of France?&#10;(A) London&#10;(B) Paris&#10;(C) Berlin&#10;(D) Madrid"
                  rows={12}
                  className="question-textarea"
                />
                <div className="char-count">
                  {questionText.length} characters
                </div>
              </div>
            ) : inputMethod === 'paste' ? (
              <div className="paste-input-section">
                <div 
                  className="paste-area"
                  onPaste={handlePaste}
                  tabIndex={0}
                >
                  {!pastedImage ? (
                    <>
                      <div className="paste-icon">📋</div>
                      <div className="paste-instruction">
                        Press <kbd>Ctrl+V</kbd> (or <kbd>Cmd+V</kbd> on Mac) to paste an image
                      </div>
                      <div className="paste-hint">
                        Take a screenshot or copy an image, then paste it here
                      </div>
                    </>
                  ) : (
                    <div className="pasted-image-preview">
                      <img 
                        src={pastedImage.preview} 
                        alt="Pasted question" 
                        className="preview-image"
                      />
                      <div className="pasted-image-info">
                        <div className="pasted-image-name">📷 {pastedImage.name}</div>
                        <div className="pasted-image-size">
                          {(pastedImage.size / 1024).toFixed(2)} KB
                        </div>
                      </div>
                      <button 
                        className="clear-pasted-btn"
                        onClick={handleClearPastedImage}
                      >
                        ✕ Clear Image
                      </button>
                    </div>
                  )}
                </div>
              </div>
            ) : (
              <div className="file-input-section">
                <div className="file-upload-area">
                  <input
                    type="file"
                    id="question-file"
                    accept=".jpg,.jpeg,.png,.pdf,.docx,.txt"
                    onChange={handleFileSelect}
                    className="file-input-hidden"
                  />
                  <label htmlFor="question-file" className="file-upload-label">
                    {selectedFile ? (
                      <>
                        <div className="file-icon">📄</div>
                        <div className="file-name">{selectedFile.name}</div>
                        <div className="file-size">
                          {(selectedFile.size / 1024).toFixed(2)} KB
                        </div>
                        <div className="file-type-badge">{fileType.toUpperCase()}</div>
                      </>
                    ) : (
                      <>
                        <div className="upload-icon">📤</div>
                        <div className="upload-text">
                          Click to upload or drag & drop
                        </div>
                        <div className="upload-formats">
                          JPG, PNG, PDF, DOCX, or TXT
                        </div>
                      </>
                    )}
                  </label>
                </div>

                {selectedFile && (
                  <button className="change-file-btn" onClick={() => {
                    setSelectedFile(null);
                    setFileType('');
                  }}>
                    Change File
                  </button>
                )}
              </div>
            )}

            {error && (
              <div className="error-message">
                ⚠️ {error}
              </div>
            )}

            <div className="action-buttons">
              <button
                className="submit-btn"
                onClick={handleSubmit}
                disabled={loading || (inputMethod === 'text' && !questionText.trim()) || (inputMethod === 'file' && !selectedFile) || (inputMethod === 'paste' && !pastedImage)}
              >
                {loading ? (
                  <>
                    <span className="spinner"></span>
                    Parsing...
                  </>
                ) : (
                  <>
                    🚀 Parse Question
                  </>
                )}
              </button>
              <button className="cancel-btn" onClick={onClose}>
                Cancel
              </button>
            </div>
          </>
        ) : (
          <div className="parsed-result">
            <div className="result-header">
              <h3>✅ Question Parsed Successfully!</h3>
            </div>

            <div className="parsed-question-display">
              <div className="parsed-field">
                <strong>Question Number:</strong>
                <span className="field-value">{parsedQuestion.question_number || 'N/A'}</span>
              </div>

              <div className="parsed-field">
                <strong>Question Type:</strong>
                <span className={`type-badge ${parsedQuestion.question_type}`}>
                  {parsedQuestion.question_type || 'unknown'}
                </span>
              </div>

              <div className="parsed-field full-width">
                <strong>Question Text:</strong>
                <div className="question-text-box">
                  {parsedQuestion.question_text}
                </div>
              </div>

              {parsedQuestion.sub_parts && parsedQuestion.sub_parts.length > 0 && (
                <div className="parsed-field full-width">
                  <strong>Sub-parts:</strong>
                  <div className="sub-parts-list">
                    {parsedQuestion.sub_parts.map((part, idx) => (
                      <span key={idx} className="sub-part-badge">{part}</span>
                    ))}
                  </div>
                </div>
              )}

              {parsedQuestion.marks && (
                <div className="parsed-field">
                  <strong>Marks:</strong>
                  <span className="marks-value">{parsedQuestion.marks}</span>
                </div>
              )}

              {parsedQuestion.has_diagram && (
                <div className="parsed-field">
                  <strong>Has Diagram:</strong>
                  <span className="diagram-indicator">📊 Yes</span>
                </div>
              )}
            </div>

            <div className="result-actions">
              <button className="parse-another-btn" onClick={handleReset}>
                📝 Parse Another Question
              </button>
              <button className="close-result-btn" onClick={onClose}>
                Close
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default SingleQuestionUpload;
