import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ChapterQuestions.css';

const ChapterQuestions = () => {
  const [papers, setPapers] = useState([]);
  const [textbooks, setTextbooks] = useState([]);
  const [selectedPaper, setSelectedPaper] = useState(null);
  const [selectedTextbook, setSelectedTextbook] = useState(null);
  const [chapters, setChapters] = useState([]);
  const [chapterStats, setChapterStats] = useState([]);
  const [selectedChapter, setSelectedChapter] = useState(null);
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });

  // Predefined textbooks with chapters and page numbers
  const defaultTextbooks = {
    'Physics': [
      {
        name: 'NCERT Physics Class 10',
        chapters: [
          { name: 'Electricity', pages: '1-20' },
          { name: 'Magnetic Effects of Current', pages: '21-40' },
          { name: 'Light - Reflection and Refraction', pages: '41-65' },
          { name: 'Human Eye and Colourful World', pages: '66-85' },
          { name: 'Sources of Energy', pages: '86-105' }
        ]
      }
    ],
    'Chemistry': [
      {
        name: 'NCERT Chemistry Class 10',
        chapters: [
          { name: 'Chemical Reactions and Equations', pages: '1-18' },
          { name: 'Acids, Bases and Salts', pages: '19-35' },
          { name: 'Metals and Non-metals', pages: '36-55' },
          { name: 'Carbon and its Compounds', pages: '56-80' },
          { name: 'Periodic Classification of Elements', pages: '81-95' }
        ]
      }
    ],
    'Biology': [
      {
        name: 'NCERT Biology Class 10',
        chapters: [
          { name: 'Life Processes', pages: '1-25' },
          { name: 'Control and Coordination', pages: '26-45' },
          { name: 'How do Organisms Reproduce', pages: '46-70' },
          { name: 'Heredity and Evolution', pages: '71-90' },
          { name: 'Our Environment', pages: '91-110' }
        ]
      }
    ],
    'Mathematics': [
      {
        name: 'NCERT Mathematics Class 10',
        chapters: [
          { name: 'Real Numbers', pages: '1-15' },
          { name: 'Polynomials', pages: '16-30' },
          { name: 'Pair of Linear Equations in Two Variables', pages: '31-50' },
          { name: 'Quadratic Equations', pages: '51-70' },
          { name: 'Arithmetic Progressions', pages: '71-85' },
          { name: 'Triangles', pages: '86-110' },
          { name: 'Coordinate Geometry', pages: '111-130' },
          { name: 'Introduction to Trigonometry', pages: '131-150' },
          { name: 'Some Applications of Trigonometry', pages: '151-165' },
          { name: 'Circles', pages: '166-185' },
          { name: 'Areas Related to Circles', pages: '186-200' },
          { name: 'Surface Areas and Volumes', pages: '201-225' },
          { name: 'Statistics', pages: '226-245' },
          { name: 'Probability', pages: '246-260' }
        ]
      }
    ]
  };

  useEffect(() => {
    fetchPapers();
  }, []);

  useEffect(() => {
    if (selectedPaper && selectedTextbook) {
      calculateChapterStats();
    }
  }, [selectedPaper, selectedTextbook]);

  const fetchPapers = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/uploaded-papers');
      setPapers(response.data);
    } catch (err) {
      console.error('Error fetching papers:', err);
      setMessage({ type: 'error', text: 'Failed to load papers' });
    }
  };

  const handlePaperSelect = async (paper) => {
    setSelectedPaper(paper);
    setSelectedTextbook(null);
    setSelectedChapter(null);
    setQuestions([]);
    setChapters([]);
    setChapterStats([]);

    // Set available textbooks based on subject
    const availableTextbooks = defaultTextbooks[paper.subject] || [];
    setTextbooks(availableTextbooks);

    if (availableTextbooks.length === 1) {
      // Auto-select if only one textbook
      handleTextbookSelect(availableTextbooks[0]);
    }

    setMessage({ type: 'info', text: `Selected: ${paper.title}` });
  };

  const handleTextbookSelect = (textbook) => {
    setSelectedTextbook(textbook);
    setSelectedChapter(null);
    setQuestions([]);
    setChapters(textbook.chapters);
    setMessage({ type: 'info', text: `Selected: ${textbook.name}` });
  };

  const calculateChapterStats = async () => {
    if (!selectedPaper || !selectedTextbook) return;

    try {
      // Fetch all questions from the selected paper
      const response = await axios.get(
        `http://localhost:5000/api/parsed-questions?paper_id=${selectedPaper.id}`
      );

      const allQuestions = response.data;

      // Calculate question count for each chapter
      const stats = selectedTextbook.chapters.map(chapter => {
        const chapterQuestions = filterQuestionsByChapter(allQuestions, chapter.name);
        return {
          ...chapter,
          questionCount: chapterQuestions.length
        };
      });

      setChapterStats(stats);
    } catch (err) {
      console.error('Error calculating chapter stats:', err);
    }
  };

  const handleChapterSelect = async (chapterData) => {
    setSelectedChapter(chapterData);
    setLoading(true);
    setMessage({ type: '', text: '' });

    try {
      // Fetch all questions from the selected paper
      const response = await axios.get(
        `http://localhost:5000/api/parsed-questions?paper_id=${selectedPaper.id}`
      );

      const allQuestions = response.data;

      // Filter questions by chapter using AI/keyword matching
      const filteredQuestions = filterQuestionsByChapter(allQuestions, chapterData.name);

      setQuestions(filteredQuestions);

      if (filteredQuestions.length === 0) {
        setMessage({ 
          type: 'warning', 
          text: `No questions found for chapter: ${chapterData.name}` 
        });
      } else {
        setMessage({ 
          type: 'success', 
          text: `Found ${filteredQuestions.length} questions for: ${chapterData.name}` 
        });
      }
    } catch (err) {
      console.error('Error fetching questions:', err);
      setMessage({ type: 'error', text: 'Failed to load questions' });
    } finally {
      setLoading(false);
    }
  };

  const filterQuestionsByChapter = (questions, chapter) => {
    // Create keywords for each chapter
    const chapterKeywords = {
      'Electricity': ['current', 'voltage', 'resistance', 'ohm', 'circuit', 'electric', 'power', 'energy', 'joule', 'watt'],
      'Magnetic Effects of Current': ['magnetic', 'magnet', 'field', 'electromagnet', 'solenoid', 'motor', 'generator', 'fleming'],
      'Light - Reflection and Refraction': ['light', 'reflection', 'refraction', 'mirror', 'lens', 'focal', 'image', 'ray', 'concave', 'convex'],
      'Human Eye and Colourful World': ['eye', 'vision', 'lens', 'retina', 'cornea', 'spectrum', 'dispersion', 'rainbow', 'prism'],
      'Sources of Energy': ['energy', 'renewable', 'solar', 'wind', 'fossil', 'fuel', 'nuclear', 'biogas', 'hydro'],
      'Chemical Reactions and Equations': ['reaction', 'equation', 'chemical', 'reactant', 'product', 'balance', 'oxidation', 'reduction'],
      'Acids, Bases and Salts': ['acid', 'base', 'salt', 'ph', 'alkali', 'neutral', 'indicator', 'litmus'],
      'Metals and Non-metals': ['metal', 'non-metal', 'metalloid', 'alloy', 'corrosion', 'reactivity', 'ore'],
      'Carbon and its Compounds': ['carbon', 'organic', 'hydrocarbon', 'alkane', 'alkene', 'alcohol', 'acid', 'ester'],
      'Life Processes': ['nutrition', 'respiration', 'transport', 'excretion', 'photosynthesis', 'digestion'],
      'Control and Coordination': ['nervous', 'hormone', 'brain', 'neuron', 'reflex', 'endocrine', 'coordination'],
      'How do Organisms Reproduce': ['reproduction', 'sexual', 'asexual', 'fertilization', 'gamete', 'zygote'],
      'Heredity and Evolution': ['heredity', 'gene', 'chromosome', 'dna', 'evolution', 'darwin', 'variation', 'inheritance'],
      'Our Environment': ['environment', 'ecosystem', 'biodegradable', 'pollution', 'ozone', 'food chain'],
      'Real Numbers': ['real number', 'rational', 'irrational', 'prime', 'composite', 'hcf', 'lcm', 'euclid'],
      'Polynomials': ['polynomial', 'degree', 'coefficient', 'zero', 'quadratic', 'cubic'],
      'Pair of Linear Equations': ['linear equation', 'simultaneous', 'substitution', 'elimination', 'cross multiplication'],
      'Quadratic Equations': ['quadratic', 'x¬≤', 'x^2', 'discriminant', 'roots', 'factorization'],
      'Arithmetic Progressions': ['ap', 'arithmetic progression', 'common difference', 'nth term', 'sum'],
      'Triangles': ['triangle', 'similar', 'congruent', 'pythagoras', 'area', 'perimeter'],
      'Coordinate Geometry': ['coordinate', 'distance', 'section', 'midpoint', 'slope', 'x-axis', 'y-axis'],
      'Trigonometry': ['sin', 'cos', 'tan', 'trigonometry', 'angle', 'Œ∏', 'theta'],
      'Circles': ['circle', 'radius', 'diameter', 'chord', 'tangent', 'secant', 'arc'],
      'Areas Related to Circles': ['area', 'circle', 'sector', 'segment', 'circumference', 'œÄ', 'pi'],
      'Surface Areas and Volumes': ['surface area', 'volume', 'cube', 'cuboid', 'cylinder', 'cone', 'sphere'],
      'Statistics': ['mean', 'median', 'mode', 'data', 'frequency', 'cumulative'],
      'Probability': ['probability', 'event', 'outcome', 'random', 'certain', 'impossible']
    };

    const keywords = chapterKeywords[chapter] || chapter.toLowerCase().split(' ');

    return questions.filter(q => {
      const questionText = q.question_text.toLowerCase();
      // Check if any keyword appears in the question
      return keywords.some(keyword => questionText.includes(keyword.toLowerCase()));
    });
  };

  return (
    <div className="chapter-questions">
      <div className="chapter-header">
        <h2>üìñ Questions by Chapter</h2>
        <p>Select a question paper, textbook, and chapter to view related questions</p>
      </div>

      {message.text && (
        <div className={`message ${message.type}`}>
          {message.text}
        </div>
      )}

      <div className="chapter-content">
        {/* Paper Selection */}
        <div className="paper-selection">
          <h3>üìÑ Select Question Paper</h3>
          <div className="papers-list">
            {papers.length === 0 ? (
              <p className="no-data">No question papers uploaded yet</p>
            ) : (
              papers.map(paper => (
                <div
                  key={paper.id}
                  className={`paper-card ${selectedPaper?.id === paper.id ? 'selected' : ''}`}
                  onClick={() => handlePaperSelect(paper)}
                >
                  <div className="paper-icon">üìù</div>
                  <div className="paper-info">
                    <h4>{paper.title}</h4>
                    <p className="paper-subject">{paper.subject}</p>
                    <p className="paper-date">
                      {new Date(paper.uploaded_at).toLocaleDateString()}
                    </p>
                  </div>
                  {selectedPaper?.id === paper.id && (
                    <div className="selected-badge">‚úì</div>
                  )}
                </div>
              ))
            )}
          </div>
        </div>

        {/* Textbook Selection */}
        {selectedPaper && textbooks.length > 0 && (
          <div className="textbook-selection">
            <h3>üìö Select Textbook</h3>
            <div className="textbooks-list">
              {textbooks.map((textbook, index) => (
                <div
                  key={index}
                  className={`textbook-card ${selectedTextbook?.name === textbook.name ? 'selected' : ''}`}
                  onClick={() => handleTextbookSelect(textbook)}
                >
                  <div className="textbook-icon">üìï</div>
                  <div className="textbook-info">
                    <h4>{textbook.name}</h4>
                    <p>{textbook.chapters.length} Chapters</p>
                  </div>
                  {selectedTextbook?.name === textbook.name && (
                    <div className="selected-badge">‚úì</div>
                  )}
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Chapter Table */}
        {selectedTextbook && chapterStats.length > 0 && (
          <div className="chapter-table-section">
            <h3>üìã Chapters from {selectedTextbook.name}</h3>
            <div className="chapter-table-container">
              <table className="chapter-table">
                <thead>
                  <tr>
                    <th>#</th>
                    <th>Chapter Name</th>
                    <th>Pages</th>
                    <th>Questions</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {chapterStats.map((chapter, index) => (
                    <tr 
                      key={index}
                      className={selectedChapter?.name === chapter.name ? 'selected-row' : ''}
                    >
                      <td className="chapter-num">{index + 1}</td>
                      <td className="chapter-name-cell">
                        <strong>{chapter.name}</strong>
                      </td>
                      <td className="pages-cell">{chapter.pages}</td>
                      <td className="count-cell">
                        <span className={`question-count ${chapter.questionCount > 0 ? 'has-questions' : 'no-questions'}`}>
                          {chapter.questionCount}
                        </span>
                      </td>
                      <td className="action-cell">
                        <button
                          className="view-btn"
                          onClick={() => handleChapterSelect(chapter)}
                          disabled={chapter.questionCount === 0}
                        >
                          {chapter.questionCount > 0 ? 'View Questions' : 'No Questions'}
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
            <div className="table-summary">
              <p>
                <strong>Total Chapters:</strong> {chapterStats.length} | 
                <strong> Total Questions:</strong> {chapterStats.reduce((sum, ch) => sum + ch.questionCount, 0)}
              </p>
            </div>
          </div>
        )}

        {/* Questions Display */}
        {selectedChapter && (
          <div className="questions-display">
            <div className="questions-header-info">
              <h3>‚ùì Questions from: {selectedChapter.name}</h3>
              <div className="chapter-meta">
                <span className="meta-item">üìñ <strong>Textbook:</strong> {selectedTextbook.name}</span>
                <span className="meta-item">üìÑ <strong>Pages:</strong> {selectedChapter.pages}</span>
                <span className="meta-item">üìù <strong>Paper:</strong> {selectedPaper.title}</span>
              </div>
            </div>

            {loading ? (
              <div className="loading">
                <div className="spinner"></div>
                <p>Loading questions...</p>
              </div>
            ) : questions.length === 0 ? (
              <div className="no-questions">
                <p>No questions found for this chapter</p>
                <p className="hint">Try selecting a different chapter</p>
              </div>
            ) : (
              <div className="questions-list">
                {questions.map((q, index) => (
                  <div key={q.id} className="question-item">
                    <div className="question-header">
                      <span className="question-number">Q{q.question_number}</span>
                      {q.question_type && (
                        <span className={`question-type ${q.question_type}`}>
                          {q.question_type.toUpperCase()}
                        </span>
                      )}
                      {q.marks && (
                        <span className="question-marks">{q.marks} marks</span>
                      )}
                      {q.has_math && (
                        <span className="math-badge">üìê Math</span>
                      )}
                      {q.has_diagram && (
                        <span className="diagram-badge">üìä Diagram</span>
                      )}
                    </div>
                    <div className="question-text">
                      {q.question_text}
                    </div>
                    {q.sub_parts && q.sub_parts.length > 0 && (
                      <div className="sub-parts">
                        <strong>Sub-parts:</strong> {q.sub_parts.join(', ')}
                      </div>
                    )}
                  </div>
                ))}
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default ChapterQuestions;
