import React, { useState } from 'react';
import axios from 'axios';
import './Login.css';

const Login = ({ onLogin }) => {
  const [username, setUsername] = useState('student1');
  const [password, setPassword] = useState('password123');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      const response = await axios.post('http://localhost:5000/api/login', {
        username,
        password
      });

      if (response.data.success) {
        onLogin(response.data.user);
      }
    } catch (err) {
      setError(err.response?.data?.error || 'Login failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const fillCredentials = (user) => {
    if (user === 'student1') {
      setUsername('student1');
      setPassword('password123');
    } else {
      setUsername('student2');
      setPassword('password123');
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="login-header">
          <h1>Academic Portal</h1>
          <p>CBSE Senior School</p>
        </div>

        <form onSubmit={handleSubmit} className="login-form">
          <div className="form-group">
            <label htmlFor="username">Username</label>
            <input
              type="text"
              id="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="Enter username"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter password"
              required
            />
          </div>

          {error && <div className="error-message">{error}</div>}

          <button type="submit" className="login-button" disabled={loading}>
            {loading ? 'Logging in...' : 'Login'}
          </button>
        </form>

        <div className="quick-login">
          <p>Quick Login:</p>
          <div className="quick-login-buttons">
            <button onClick={() => fillCredentials('student1')} className="quick-btn">
              Student 1
            </button>
            <button onClick={() => fillCredentials('student2')} className="quick-btn">
              Student 2
            </button>
          </div>
        </div>

        <div className="login-footer">
          <p className="credentials-info">
            <strong>Demo Credentials:</strong><br />
            Username: student1 / student2<br />
            Password: password123
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
