import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ChapterQuestions.css';

const ChapterQuestions = () => {
  const [papers, setPapers] = useState([]);
  const [selectedPaper, setSelectedPaper] = useState(null);
  const [chapters, setChapters] = useState([]);
  const [selectedChapter, setSelectedChapter] = useState(null);
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });

  // Predefined chapters for CBSE subjects
  const defaultChapters = {
    'Physics': [
      'Electricity', 'Magnetic Effects of Current', 'Light - Reflection and Refraction',
      'Human Eye and Colourful World', 'Sources of Energy', 'Electricity and Magnetism'
    ],
    'Chemistry': [
      'Chemical Reactions and Equations', 'Acids, Bases and Salts', 'Metals and Non-metals',
      'Carbon and its Compounds', 'Periodic Classification of Elements'
    ],
    'Biology': [
      'Life Processes', 'Control and Coordination', 'How do Organisms Reproduce',
      'Heredity and Evolution', 'Our Environment'
    ],
    'Mathematics': [
      'Real Numbers', 'Polynomials', 'Pair of Linear Equations in Two Variables',
      'Quadratic Equations', 'Arithmetic Progressions', 'Triangles', 'Coordinate Geometry',
      'Introduction to Trigonometry', 'Some Applications of Trigonometry', 'Circles',
      'Areas Related to Circles', 'Surface Areas and Volumes', 'Statistics', 'Probability'
    ]
  };

  useEffect(() => {
    fetchPapers();
  }, []);

  const fetchPapers = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/uploaded-papers');
      setPapers(response.data);
    } catch (err) {
      console.error('Error fetching papers:', err);
      setMessage({ type: 'error', text: 'Failed to load papers' });
    }
  };

  const handlePaperSelect = async (paper) => {
    setSelectedPaper(paper);
    setSelectedChapter(null);
    setQuestions([]);

    // Set chapters based on subject
    const paperChapters = defaultChapters[paper.subject] || [];
    setChapters(paperChapters);

    setMessage({ type: 'info', text: `Selected: ${paper.title}` });
  };

  const handleChapterSelect = async (chapter) => {
    setSelectedChapter(chapter);
    setLoading(true);
    setMessage({ type: '', text: '' });

    try {
      // Fetch all questions from the selected paper
      const response = await axios.get(
        `http://localhost:5000/api/parsed-questions?paper_id=${selectedPaper.id}`
      );

      const allQuestions = response.data;

      // Filter questions by chapter using AI/keyword matching
      const filteredQuestions = filterQuestionsByChapter(allQuestions, chapter);

      setQuestions(filteredQuestions);

      if (filteredQuestions.length === 0) {
        setMessage({ 
          type: 'warning', 
          text: `No questions found for chapter: ${chapter}` 
        });
      } else {
        setMessage({ 
          type: 'success', 
          text: `Found ${filteredQuestions.length} questions for: ${chapter}` 
        });
      }
    } catch (err) {
      console.error('Error fetching questions:', err);
      setMessage({ type: 'error', text: 'Failed to load questions' });
    } finally {
      setLoading(false);
    }
  };

  const filterQuestionsByChapter = (questions, chapter) => {
    // Create keywords for each chapter
    const chapterKeywords = {
      'Electricity': ['current', 'voltage', 'resistance', 'ohm', 'circuit', 'electric', 'power', 'energy', 'joule', 'watt'],
      'Magnetic Effects of Current': ['magnetic', 'magnet', 'field', 'electromagnet', 'solenoid', 'motor', 'generator', 'fleming'],
      'Light - Reflection and Refraction': ['light', 'reflection', 'refraction', 'mirror', 'lens', 'focal', 'image', 'ray', 'concave', 'convex'],
      'Human Eye and Colourful World': ['eye', 'vision', 'lens', 'retina', 'cornea', 'spectrum', 'dispersion', 'rainbow', 'prism'],
      'Sources of Energy': ['energy', 'renewable', 'solar', 'wind', 'fossil', 'fuel', 'nuclear', 'biogas', 'hydro'],
      'Chemical Reactions and Equations': ['reaction', 'equation', 'chemical', 'reactant', 'product', 'balance', 'oxidation', 'reduction'],
      'Acids, Bases and Salts': ['acid', 'base', 'salt', 'ph', 'alkali', 'neutral', 'indicator', 'litmus'],
      'Metals and Non-metals': ['metal', 'non-metal', 'metalloid', 'alloy', 'corrosion', 'reactivity', 'ore'],
      'Carbon and its Compounds': ['carbon', 'organic', 'hydrocarbon', 'alkane', 'alkene', 'alcohol', 'acid', 'ester'],
      'Life Processes': ['nutrition', 'respiration', 'transport', 'excretion', 'photosynthesis', 'digestion'],
      'Control and Coordination': ['nervous', 'hormone', 'brain', 'neuron', 'reflex', 'endocrine', 'coordination'],
      'How do Organisms Reproduce': ['reproduction', 'sexual', 'asexual', 'fertilization', 'gamete', 'zygote'],
      'Heredity and Evolution': ['heredity', 'gene', 'chromosome', 'dna', 'evolution', 'darwin', 'variation', 'inheritance'],
      'Our Environment': ['environment', 'ecosystem', 'biodegradable', 'pollution', 'ozone', 'food chain'],
      'Real Numbers': ['real number', 'rational', 'irrational', 'prime', 'composite', 'hcf', 'lcm', 'euclid'],
      'Polynomials': ['polynomial', 'degree', 'coefficient', 'zero', 'quadratic', 'cubic'],
      'Pair of Linear Equations': ['linear equation', 'simultaneous', 'substitution', 'elimination', 'cross multiplication'],
      'Quadratic Equations': ['quadratic', 'x¬≤', 'x^2', 'discriminant', 'roots', 'factorization'],
      'Arithmetic Progressions': ['ap', 'arithmetic progression', 'common difference', 'nth term', 'sum'],
      'Triangles': ['triangle', 'similar', 'congruent', 'pythagoras', 'area', 'perimeter'],
      'Coordinate Geometry': ['coordinate', 'distance', 'section', 'midpoint', 'slope', 'x-axis', 'y-axis'],
      'Trigonometry': ['sin', 'cos', 'tan', 'trigonometry', 'angle', 'Œ∏', 'theta'],
      'Circles': ['circle', 'radius', 'diameter', 'chord', 'tangent', 'secant', 'arc'],
      'Areas Related to Circles': ['area', 'circle', 'sector', 'segment', 'circumference', 'œÄ', 'pi'],
      'Surface Areas and Volumes': ['surface area', 'volume', 'cube', 'cuboid', 'cylinder', 'cone', 'sphere'],
      'Statistics': ['mean', 'median', 'mode', 'data', 'frequency', 'cumulative'],
      'Probability': ['probability', 'event', 'outcome', 'random', 'certain', 'impossible']
    };

    const keywords = chapterKeywords[chapter] || chapter.toLowerCase().split(' ');

    return questions.filter(q => {
      const questionText = q.question_text.toLowerCase();
      // Check if any keyword appears in the question
      return keywords.some(keyword => questionText.includes(keyword.toLowerCase()));
    });
  };

  return (
    <div className="chapter-questions">
      <div className="chapter-header">
        <h2>üìö Study by Chapters</h2>
        <p>Select a question paper and chapter to view related questions</p>
      </div>

      {message.text && (
        <div className={`message ${message.type}`}>
          {message.text}
        </div>
      )}

      <div className="chapter-content">
        {/* Paper Selection */}
        <div className="paper-selection">
          <h3>üìÑ Select Question Paper</h3>
          <div className="papers-list">
            {papers.length === 0 ? (
              <p className="no-data">No question papers uploaded yet</p>
            ) : (
              papers.map(paper => (
                <div
                  key={paper.id}
                  className={`paper-card ${selectedPaper?.id === paper.id ? 'selected' : ''}`}
                  onClick={() => handlePaperSelect(paper)}
                >
                  <div className="paper-icon">üìù</div>
                  <div className="paper-info">
                    <h4>{paper.title}</h4>
                    <p className="paper-subject">{paper.subject}</p>
                    <p className="paper-date">
                      {new Date(paper.uploaded_at).toLocaleDateString()}
                    </p>
                  </div>
                  {selectedPaper?.id === paper.id && (
                    <div className="selected-badge">‚úì</div>
                  )}
                </div>
              ))
            )}
          </div>
        </div>

        {/* Chapter Selection */}
        {selectedPaper && (
          <div className="chapter-selection">
            <h3>üìñ Select Chapter</h3>
            <div className="chapters-list">
              {chapters.length === 0 ? (
                <p className="no-data">No chapters available for this subject</p>
              ) : (
                chapters.map((chapter, index) => (
                  <div
                    key={index}
                    className={`chapter-card ${selectedChapter === chapter ? 'selected' : ''}`}
                    onClick={() => handleChapterSelect(chapter)}
                  >
                    <div className="chapter-number">{index + 1}</div>
                    <div className="chapter-name">{chapter}</div>
                    {selectedChapter === chapter && (
                      <div className="selected-badge">‚úì</div>
                    )}
                  </div>
                ))
              )}
            </div>
          </div>
        )}

        {/* Questions Display */}
        {selectedChapter && (
          <div className="questions-display">
            <h3>‚ùì Questions from: {selectedChapter}</h3>

            {loading ? (
              <div className="loading">
                <div className="spinner"></div>
                <p>Loading questions...</p>
              </div>
            ) : questions.length === 0 ? (
              <div className="no-questions">
                <p>No questions found for this chapter</p>
                <p className="hint">Try selecting a different chapter</p>
              </div>
            ) : (
              <div className="questions-list">
                {questions.map((q, index) => (
                  <div key={q.id} className="question-item">
                    <div className="question-header">
                      <span className="question-number">Q{q.question_number}</span>
                      {q.question_type && (
                        <span className={`question-type ${q.question_type}`}>
                          {q.question_type.toUpperCase()}
                        </span>
                      )}
                      {q.marks && (
                        <span className="question-marks">{q.marks} marks</span>
                      )}
                      {q.has_math && (
                        <span className="math-badge">üìê Math</span>
                      )}
                      {q.has_diagram && (
                        <span className="diagram-badge">üìä Diagram</span>
                      )}
                    </div>
                    <div className="question-text">
                      {q.question_text}
                    </div>
                    {q.sub_parts && q.sub_parts.length > 0 && (
                      <div className="sub-parts">
                        <strong>Sub-parts:</strong> {q.sub_parts.join(', ')}
                      </div>
                    )}
                  </div>
                ))}
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default ChapterQuestions;
